ng>Email:</strong> ".$email_address;	$email_message = wordwrap($message, 70, "\r\n");	$email_message2 = wordwrap($message2, 70, "\r\n");	$email_headers = "MIME-Version: 1.0" . "\r\n";	$email_headers .= "Content-type:text/html;charset=UTF-8" . "\r\n";	$email_headers .= 'From: Eastcoast to Westcoast Admin<administrator@eastcoast2westcoast.com>' . "\r\n";	$date = date("Y/m/d");	$trip_location = 4;		$disney_insert = "INSERT INTO distribution_list (`user_id`, `first_name`, `last_name`, `email_address`, `signup_date`, `trip_location`, `user_updated`)				VALUES('', '$first_name', '$last_name', '$email_address', '$date', '$trip_location', 'System');";		echo "<div id=confirmation_form><p>Hey ".$first_name.", thanks for signing up for the Disney World Vacation</p>";	echo "<p>Please check your email for additional information.</p>";	echo "<p>Email Provided: ".$email_address."</p>";	echo "<p>Participants: <b><u>".$num_adults."</u></b> adult(s) and <b><u>".$num_kids."</u></b> kid(s). </p>";	echo "</div>";		mysqli_query($connect, $disney_insert) or die ("Unable to run insert query:" .mysqli_errno($connect));	$dbUser_id = mysqli_insert_id($connect);	$disneyPayment_insert = "INSERT INTO disney_payment (`user_id`, `firstname`, `lastname`, `adults`, `children`, `children_age`, `amt_paid`, `amt_outstanding`, `room_type`, `bed_type`, `email`, `addt_notes`, `date_added`, `date_updated`, `user_updated`)							VALUES ('$dbUser_id', '$first_name', '$last_name', '$num_adults', '$num_kids', '$kids_age', '$', '$', '$room_type', '$bed_type', '$email_address', '$addt_notes', '$date', '$date', 'System')";									mysqli_query($connect, $disneyPayment_insert) or die ("Unable to run disney payment query:" .mysqli_error($connect));	mail($to, $subject, $email_message, $email_headers);	mail($admin, $subject, $email_message, $email_headers);	mysqli_close($connect);?>