<div id="modals">	<?php if(isset($_GET["view_gallery"])) { ?>		<div class="maine_overlay_pictures">			<button class="closeBtn">X</button>		</div>		<div class="maine_modal_picture">			<?php $getLocation = find_event_by_name(str_ireplace("_", " ", $_GET["view_gallery"])); ?>			<?php $getPictures = find_all_pictures_by_id($getLocation["trip_id"]); ?>			<?php if(mysqli_num_rows($getPictures) > 0) { ?>				<div class="picture_modal_content">					<div id="<?php echo str_ireplace(" ", "_", strtolower($getLocation["trip_location"])); ?>_pictures">						<?php while($showPicture = mysqli_fetch_assoc($getPictures)) { ?>							<h2 class="pictureCaption"><?php echo $showPicture["picture_caption"] ?></h2>							<img  src="images/<?php echo $showPicture["picture_name"] ?>" id="<?php echo $showPicture["picture_id"] ?>" class="<?php echo str_ireplace(" ", "_", strtolower($getLocation["trip_location"])); ?>_picture" />						<?php } ?>					</div>					<button class="prevLeft"><span class="spanArrow">&#8672;</span>Prev</button>					<button class="nextRight">Next<span class="spanArrow">&#8674;</span></button>					<p class="additionalPictures">If you have any pictures or videos that you want posted, please send them to <a class="mailToLink" href="mailto:administrator@eastcoast2westcoast.com?subject=<?php echo str_ireplace(" ", "_", ucwords($getLocation["trip_location"])); ?>_Pictures">administrator@eastcoast2westcoast.com</a></p>				</div>			<?php } else { ?>				<div class="pictureModalNoContent">					<div id="<?php echo str_ireplace(" ", "_", strtolower($getLocation["trip_location"])); ?>_pictures">						<h2>No pictures have been added yet for this trip</h2>					</div>					<p class="additionalPictures">If you have any pictures or videos that you want posted, please send them to <a class="mailToLink" href="mailto:administrator@eastcoast2westcoast.com?subject=<?php echo str_ireplace(" ", "_", ucwords($getLocation["trip_location"])); ?>_Pictures">administrator@eastcoast2westcoast.com</a></p>				</div>				<?php } ?>		</div>	<?php } ?>	<?php if(isset($_GET["view_videos"])) { ?>		<div class="maine_overlay_pictures">			<button class="closeBtn">X</button>		</div>		<div class="maine_modal_videos">			<?php $getLocation = find_event_by_name(str_ireplace("_", " ", $_GET["view_videos"])); ?>			<?php $getPictures = find_all_videos_by_id($getLocation["trip_id"]); ?>			<?php if(mysqli_num_rows($getPictures) > 0) { ?>				<div class="videos_modal_content">					<div class="indVideo">						<?php while($showPicture = mysqli_fetch_assoc($getPictures)) { ?>							<video height="300" id="video_<?php echo $showPicture["video_id"]; ?>" class="<?php echo str_ireplace(" ", "_", strtolower($getLocation["trip_location"])); ?>_video" poster="images/<?php echo $showPicture["video_poster"]; ?>" preload="none" controls >								<source src="videos/<?php echo $showPicture["video_link"] ?>" />							</video>						<?php } ?>					</div>				</div>			<?php } else { ?>				<div class="pictureModalNoContent">					<div id="<?php echo str_ireplace(" ", "_", strtolower($getLocation["trip_location"])); ?>_videos">						<h2>No videos have been added yet for this trip</h2>					</div>				</div>				<?php } ?>		</div>	<?php } ?>	<div id="loading_image">		<img src="../public/images/ajax-loader.gif" class="" />	</div></div>	